mapper.select:
select * from (select row_number() over (order by B_DTT desc) ronum,
                      cat_cd, b_dtt, b_title, b_content, u_id
                from board_tb where cat_cd = #{cat_cd})
where ronum between #{rowStart} and #{rowEnd}
order by B_DTT desc

select * from (select row_number() over (order by B_DTT desc) ronum,
                      cat_cd, b_dtt, b_title, b_content, u_id
                from board_tb where B_TITLE like '%' || #{keyWord} || '%')
where ronum between #{rowStart} and #{rowEnd} and cat_cd = #{cat_cd}
order by B_DTT desc

mapper.update:
update board_tb
set b_title = #{b_title}, b_content = #{b_content}, cat_cd = #{cat_cd}
where b_dtt = #{b_dtt}
